sp_widget.client_script:
function(spModal) {
	/* widget controller */
	var c = this;
	var shared = {};

	c.server.update().then(function(){
		c.data.action = undefined;
	});

	c.refresh = function() {
		c.data.action = "refreshList";
		c.server.update().then(function(){
			c.data.action = undefined;
		});
	};

	c.remove = function(number,index){
		c.data.number = number;
		c.data.index = index;
		c.data.action = "removeElement";
		c.server.update().then(function(){
			c.data.action = undefined;
		});
	};

	c.add = function() {
		c.data.action = "addElement";
		c.server.update().then(function(){
			c.data.action = undefined;
		});
	};

	c.edit = function(number,size) {
		spModal.open({
			size : size,
			title : 'Edit Git Record',
			widget : '2f676f370f976300ebdfba8ce1050e39',
			shared : shared,
			 buttons: [
                {label:'y', hide: true},
                {label:'n', hide: true}]
		}).then(function(){
			c.data.action = "editElement";
			c.server.update().then(function(){
				c.data.action = undefined;
			});
		});
	};
}

